type Imported = ImportedLocal | ImportedNpx | ImportFailure;
interface ImportedLocal {
    kind: "local";
    resolved: ModuleNamespaceObject;
}
interface ImportedNpx {
    kind: "npx";
    resolved: ModuleNamespaceObject;
}
interface ImportFailure {
    kind: "failure";
    local: Error;
    npx: Error;
}
/** @see https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/import#module_namespace_object */
type ModuleNamespaceObject = object;

export type { ImportFailure, Imported, ImportedLocal, ImportedNpx, ModuleNamespaceObject };
